<form action="password.php" method="post">
    <fieldset>
        <div class="form-group">
            <input class="form-control" name="old" placeholder="Old password" type="password"/>
	    </div>
	    <br/>
	    <!-- implement some fancy JS client side password checking with green for yes, red for no -->
	    <div class="form-group">
            <input class="form-control" type="password" name="new" placeholder="New password" id="pass1" style="background-image: url(data:image/png;base64,); background-attachment: background-position: 100% 50%; background-repeat: no-repeat;">
        </div>
        <div class="form-group">
            <input class="form-control" type="password" name="confirmation" placeholder="Confirm password" id="pass2" onkeyup="checkPass(); return false;" style="background-image: url(data:image/png;base64,); background-attachment: background-position: 100% 50%; background-repeat: no-repeat;">
        </div>
        <br/>
        <div class="form-group">    
            <span class="form-control" id="confirmMessage" class="confirmMessage"></span>
        </div>
        <div class="form-group">
